package com.example.valentinaburjan.grabthescreen_androidapp;

import android.app.Activity;
import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.net.ConnectivityManager;
import android.net.NetworkInfo;
import android.os.AsyncTask;
import android.os.Bundle;
import android.os.RemoteException;
import android.provider.Settings;
import android.util.Base64;
import android.util.Log;
import android.view.Menu;
import android.view.MenuItem;
import android.widget.EditText;
import android.widget.ImageView;
import android.widget.TextView;

import com.estimote.sdk.Beacon;
import com.estimote.sdk.BeaconManager;
import com.estimote.sdk.Region;
import com.estimote.sdk.Utils;
import com.estimote.sdk.utils.EstimoteBeacons;

import org.apache.http.HttpResponse;
import org.apache.http.client.HttpClient;
import org.apache.http.client.methods.HttpGet;
import org.apache.http.impl.client.DefaultHttpClient;
import org.json.JSONArray;
import org.json.JSONException;
import org.json.JSONObject;

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStream;
import java.io.InputStreamReader;
import java.util.List;


public class MainActivity extends Activity implements BeaconManager.RangingListener, BeaconManager.ServiceReadyCallback {

    private final double PROXIMITY_TABLE = 0.15D;
    private final String PICTURE_URL = "http://141.19.142.50:28017/gts/pictures/";

    public String car_type;
    public String car_price;


    // Beacon
    private BeaconManager beaconManager;
    private Region estimoteBeacon_MINT;

    // Json
    //public JSONObject jsonObject, row;
    public JSONArray jsonArray;

    // GUI Elemente
    public EditText etResponse;
    public ImageView imageView;
    public TextView tvIsConnected, tvCarName, tvCarType, tvCarPrice;


    @Override
    protected void onCreate(Bundle savedInstanceState) {

        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        // Estimote iBeacon Mint Cocktail mit Major = 4509 und Minor=17386
        estimoteBeacon_MINT = new Region("regionId", EstimoteBeacons.ESTIMOTE_PROXIMITY_UUID, 4509, 17386);
        beaconManager = new BeaconManager(this);
        beaconManager.setRangingListener(this);

        // get reference to the views
        etResponse = (EditText) findViewById(R.id.etResponse);
        imageView = (ImageView) findViewById(R.id.ivImageCar);
        tvIsConnected = (TextView) findViewById(R.id.tvIsConnected);
        tvCarName = (TextView) findViewById(R.id.tvCarName);
        tvCarType = (TextView) findViewById(R.id.tvCarType);
        tvCarPrice = (TextView) findViewById(R.id.tvCarPrice);

        // call AsynTask to perform network operation on separate thread
        //new HttpAsyncTask().execute("http://141.19.142.50:28017/gts/pictures/");

    }

    @Override
    protected void onStart() {
        super.onStart();
        beaconManager.connect(this);
    }

    @Override
    protected void onStop() {
        try {
            super.onStop();
            beaconManager.stopRanging(estimoteBeacon_MINT);
            beaconManager.disconnect();
        } catch (RemoteException e) {
            e.printStackTrace();
        }
    }

    @Override
    public boolean onCreateOptionsMenu(Menu menu) {
        // Inflate the menu; this adds items to the action bar if it is present.
        getMenuInflater().inflate(R.menu.menu_main, menu);
        return true;
    }

    @Override
    public boolean onOptionsItemSelected(MenuItem item) {
        // Handle action bar item clicks here. The action bar will
        // automatically handle clicks on the Home/Up button, so long
        // as you specify a parent activity in AndroidManifest.xml.
        int id = item.getItemId();

        //noinspection SimplifiableIfStatement
        if (id == R.id.action_settings) {
            return true;
        }

        return super.onOptionsItemSelected(item);
    }


    /**
     * This is where the magic happens.
     * region - Region that was used for ranging.
     * beacons - List of beacons sorted by accuracy.
     */
    @Override
    public void onBeaconsDiscovered(Region region, List<Beacon> beacons) {
        Log.d("TEST", beacons.toString());

        // check if you are connected or not to Server
        if (isConnected()) {
            tvIsConnected.setBackgroundColor(0xff00cc00);
            tvIsConnected.setText("You are connected to Server");
        } else {
            tvIsConnected.setText("You are NOT connected to Server");
        }

        for (Beacon beacon : beacons) {
            double distance_to_beacon = Utils.computeAccuracy(beacon);
            if (distance_to_beacon < PROXIMITY_TABLE) {
                tvIsConnected.setText("You are connected to iBeacon");
                new HttpAsyncTask().execute(PICTURE_URL);
            } else {
                System.out.println("The MINT beacon is NOT in immediate proximity. Distance: " + distance_to_beacon);
            }
        }
    }

    /**
     * Don't touch
     */
    @Override
    public void onServiceReady() {
        try {
            beaconManager.startRanging(estimoteBeacon_MINT);
        } catch (RemoteException e) {
            e.printStackTrace();
        }
    }

    @Override
    public void onDestroy() {
        super.onDestroy();
        beaconManager.disconnect();
    }

    public static String GET(String url) {
        InputStream inputStream = null;
        String result = "";
        try {
            HttpResponse httpResponse = new DefaultHttpClient().execute(new HttpGet(url));
            inputStream = httpResponse.getEntity().getContent();

            // convert inputstream to string
            if (inputStream != null) {
                result = convertInputStreamToString(inputStream);
            } else
                result = "Did not work!";

        } catch (Exception e) {
            Log.d("InputStream", e.getLocalizedMessage());
        } finally {
            if (inputStream != null) {
                try {
                    inputStream.close();
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
        }

        return result;
    }

    private static String convertInputStreamToString(InputStream inputStream) throws IOException {
        BufferedReader bufferedReader = new BufferedReader(new InputStreamReader(inputStream));
        String line = "";
        String result = "";

        while ((line = bufferedReader.readLine()) != null) {
            result += line;
        }

        return result;
    }

    public boolean isConnected() {
        ConnectivityManager connMgr = (ConnectivityManager) getSystemService(Activity.CONNECTIVITY_SERVICE);
        NetworkInfo networkInfo = connMgr.getActiveNetworkInfo();
        return networkInfo != null && networkInfo.isConnected();
    }

    private class HttpAsyncTask extends AsyncTask<String, Void, String> {
        @Override
        protected String doInBackground(String... urls) {
            return GET(urls[0]);
        }

        // onPostExecute displays the results of the AsyncTask.
        @Override
        protected void onPostExecute(String result) {
            try {
                JSONObject json = new JSONObject(result);
                jsonArray = json.getJSONArray("rows");
                JSONObject row = jsonArray.getJSONObject(jsonArray.length() - 1);

                tvCarName.setText(row.getString("name"));
                tvCarType.setText(car_type = row.getString("type"));
                tvCarPrice.setText(car_price = row.getString("price"));

                imageView.setImageBitmap(decodeBase64(getPictureStream(row)));
                imageView.setScaleType(ImageView.ScaleType.FIT_XY);

                etResponse.setText(row.toString());

            } catch (JSONException e) {
                e.printStackTrace();
                etResponse.setText("Ups! Etwas ist schief gelaufen!");
            }
        }
    }

    /**
     * Decodiert base64-String und setzt ImageView entsprechend
     */
    private Bitmap decodeBase64(String base64) {
        byte[] decodedString = Base64.decode(base64, Base64.DEFAULT);
        return BitmapFactory.decodeByteArray(decodedString, 0, decodedString.length);
    }

    /**
     * Extract the picture from a json object; return fallback default picture if not present.
     */
    private String getPictureStream(JSONObject json) throws JSONException {
        try {
            if (json.has("image")) {
                return json.getString("image");
            } else {
                // TODO: as soon as the real picture is working, remove this
                return "";
            }
        } catch (JSONException ex) {
            throw ex;
        }
    }
}

